import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/sessions')
      .then(response => response.json())
      .then(data => setSessions(data))
      .catch(error => console.log('Error:', error));
  }, []);

  return (
    <div className="app-container">
      <h1>My Hobby Sessions</h1>
      {sessions.length === 0 ? (
        <div className="no-sessions">
          No sessions available yet. Create your first session!
        </div>
      ) : (
        sessions.map(session => (
          <div key={session.id} className="session-card">
            <h3>{session.title}</h3>
            <p><strong>Date:</strong> {session.date}</p>
            <p><strong>Time:</strong> {session.time}</p>
            {session.description && <p><strong>Description:</strong> {session.description}</p>}
            <p><strong>Participants:</strong> {session.attendees?.length || 0}/{session.maxParticipants}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
